var feedbackOverlay=document.querySelector(".feedback-overlay"),feedbackForm=document.querySelector(".feedback-form"),btnFeedbackShow=document.querySelector(".map-info .btn"),btnFeedbackClose=document.querySelector(".feedback-close");function showFeedbackForm(a){a.preventDefault();feedbackOverlay.classList.add("feedback-overlay-show");feedbackForm.classList.add("feedback-form-show")}
function closeFeedbackForm(a){a.preventDefault();feedbackOverlay.classList.remove("feedback-overlay-show");feedbackForm.classList.remove("feedback-form-show")}btnFeedbackShow.addEventListener("click",showFeedbackForm);btnFeedbackClose.addEventListener("click",closeFeedbackForm);